    //           <img src={`images/${item.icon}`} alt='logo' />
    //           <li
    //             className={`!mt-0 text-[14px] font-[500] text-center  `}
    //           >
    //             {item.name}
    //           </li>
    //         </div>
    //       ))}
    //     </ul>
    //   </aside>
    // </div>
    <aside className="w-1/5 bg-gray-900 text-white h-screen hidden lg:flex flex-col items-center pt-5">
      <img src="images/site-logo.svg" alt="logo" className="mb-4 w-12 h-12" />
      <ul className="w-full">
        {menuItems.map((item) => (
          <li
            key={item.id}
            onClick={() => setSelectedContent(item)}
            className={`cursor-pointer p-3 flex flex-col items-center ${
              selectedContent.id === item.id ? "bg-gray-700 border-l-4 border-white" : ""
            }`}
          >
            <img src={`images/${item.icon}`} alt="icon" className="w-6 h-6" />
            <span className="text-sm">{item.name}</span>
          </li>
        ))}
      </ul>
    </aside>
  )
}

export default Sidebar
