import { useState } from "react";

import NavBar from "./Navbar";
import Sidebar from "./Sidebar";


import Rightbar from "./Rightbar";
import MainSectior from "./MainSection";

const menuItems = [
  { id: 1, name: "Dashboard", content: "Welcome to the Dashboard!" },
  { id: 2, name: "Profile", content: "This is your Profiqeqeqeqwle page." },
  { id: 3, name: "Settings", content: "Manage your settings here." },
];
export default function Dashboard() {
  const [selectedContent, setSelectedContent] = useState(menuItems[0].content);

  return (
    <div className="h-screen flex flex-col">
      {/* Navbar */}
     <NavBar />

      <div className="flex flex-grow pt-16">
        {/* Sidebar */}
        <Sidebar setSelectedContent={(val) =>setSelectedContent(val)} menuItems={menuItems} />

        {/* Main Section */}
       <MainSectior activeMenu={selectedContent} />

        {/* Right Bar */}
        <Rightbar />
      </div>
    </div>
  );
}
