import { useState } from "react";

import NavBar from "./Navbar";
import Sidebar from "./Sidebar";
import Rightbar from "./Rightbar";
import MainSectior from "./MainSection";
import { MenuItem } from "../constant/types";

const menuItems = [
  { id: 1, name: "Dashboard", content: "Welcome to the Dashboard!", icon:'Home.svg' },
  { id: 2, name: "Accounting", content: "This is your Profiqeqeqeqwle page.",icon:'account.svg' },
  { id: 3, name: "Banking", content: "Banking your settings here.",icon:'bank.svg' },
  { id: 4, name: "Reports", content: "Reports your settings here.",icon:'report.svg' },
  { id: 5, name: "Contacts", content: "Contacts your settings here.",icon:'contact.svg' },
  { id: 6, name: "Invoicing", content: "Contacts your settings here.",icon:'invoice.svg' },
  { id: 7, name: "Queries", content: "Queries your settings here.",icon:'querie.svg' },
];

export default function Dashboard() {
  const [selectedContent, setSelectedContent] = useState<MenuItem>(menuItems[0]);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  return (
    <div className="h-screen flex flex-col">
    {/* Navbar */}
    <NavBar />

    <div className="flex flex-grow">
      {/* Sidebar */}
      <aside
        className={`bg-blue-500 text-white h-screen fixed md:relative top-16 transition-all ${
          sidebarOpen ? "w-[15%] md:w-[7%]" : "w-0 md:w-[7%]"
        }`}
      >
        <button
          className="md:hidden text-white p-2 absolute right-[-30px] top-4 bg-blue-500 rounded"
          onClick={() => setSidebarOpen(!sidebarOpen)}
        >
          {sidebarOpen ? "✖" : "☰"}
        </button>

        {sidebarOpen && (
          <div className="p-4">
            <div className="flex justify-center mb-4">
              <img src={"images/site-logo.svg"} alt="logo" />
            </div>
            <ul className="space-y-4">
              {menuItems.map((item) => (
                <div
                  key={item.id}
                  onClick={() => setSelectedContent(item)}
                  className={`cursor-pointer pt-3 pb-3 flex items-center flex-col ${
                    selectedContent.id === item.id &&
                    "bg-purple-700 border-l-4 border-white"
                  }`}
                >
                  <img src={`images/${item.icon}`} alt="logo" />
                  <li className="text-[14px] font-medium text-center">
                    {item.name}
                  </li>
                </div>
              ))}
            </ul>
          </div>
        )}
      </aside>

      {/* Main Section */}
      <main className="flex-grow p-5 pt-[120px] bg-gray-100 h-screen overflow-auto">
        <div className="bg-white pt-[26px] pr-[31px] pl-[21px] pb-[21px]">
          <div className="flex items-center justify-end">
            <button className="text-[14px] font-medium w-fit px-4 py-2 bg-blue-500 text-white">
              Add to Expenses Report
            </button>
          </div>
        </div>
      </main>

      {/* Right Sidebar */}
      <aside className="w-[10%] md:w-[12%] bg-gray-200 p-4 h-screen fixed md:relative top-16 right-0 md:block hidden">
        <p className="text-sm">Right Sidebar Content</p>
      </aside>
    </div>
  </div>
  );
}
