import { useState } from "react";
// import { Clipboard } from "lucide-react";

const EasyUpload = () => {
  const [activeTab, setActiveTab] = useState("Receipts");
  const emailSingle = "abc.single@gmail.com";
  const emailMultiple = "abc.multiple@gmail.com";

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    alert("Copied to clipboard!");
  };

  return (
    <div className="max-w-lg  bg-white p-6 rounded-xl shadow-md">
      <div className="flex space-x-4 border-b pb-2">
        {['Bills', 'Receipts', 'Bank'].map(tab => (
          <button
            key={tab}
            className={`px-4 py-2 font-medium ${activeTab === tab ? 'text-purple-600 border-b-2 border-purple-600' : 'text-gray-500'}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="mt-4">
        <p className="text-gray-500">Document Owner</p>
        <p className="font-semibold">Accountant 01</p>
        <p className="text-gray-500 mt-4">Client</p>
        <select className="w-full border rounded-md p-2">
          <option>Select Client</option>
        </select>
      </div>

      <div className="border-dashed border-2 border-gray-300 rounded-md mt-6 p-6 text-center">
        <div className="flex justify-center">
          <div className="bg-gray-100 p-4 rounded-full">
            ðŸ“¤
          </div>
        </div>
        <p className="text-purple-600 font-medium mt-2">Drag & Drop or Choose file to upload</p>
        <p className="text-gray-400 text-sm">JPG, PNG, PDF, CSV</p>
      </div>

      <button className="w-full mt-4 bg-purple-600 text-white py-2 rounded-md hover:bg-purple-700">
        Add Receipt
      </button>

      <div className="mt-4">
        <p className="text-gray-500">Send Over Email</p>
        <div className="flex items-center justify-between mt-2">
          <p><span className="font-semibold">Single: </span>{emailSingle}</p>
          {/* <Clipboard className="w-5 h-5 cursor-pointer" onClick={() => copyToClipboard(emailSingle)} /> */}
        </div>
        <div className="flex items-center justify-between mt-2">
          <p><span className="font-semibold">Multiple: </span>{emailMultiple}</p>
          {/* <Clipboard className="w-5 h-5 cursor-pointer" onClick={() => copyToClipboard(emailMultiple)} /> */}
        </div>
      </div>
    </div>
  );
};

export default EasyUpload;
