import React, { useState } from 'react';

const tabs = [
    { label: 'All Documents', value: 'allDocuments' },
    { label: 'Bills', value: 'bills' },
    { label: 'Receipts', value: 'receipts' }
]

const documents = [
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 1', type: 'bill' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 2', type: 'bill' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 3', type: 'bill' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 1', type: 'receipt' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 3', type: 'receipt' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 3', type: 'receipt' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 1', type: 'bill' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 4', type: 'receipt' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 4', type: 'bill' },
]
export default function CustomTabs() {

    const [active, setActive] = useState('allDocuments');

    const handleClick = (tab: any) => {
        setActive(tab.value);
    };

    return (
        <div className="">
            <div>
                {
                    (tabs || []).map((tab) => {
                        return (
                            <button
                                className={`text-[14px] h-[40px] text-velvel_curtain w-[112px] font-bold ${active === tab.value ? 'border-2 border-[#ECECF2] rounded-[8px]' : ''}`}
                                id={tab.value}
                                onClick={() => handleClick(tab)}
                            >
                                {tab.label}
                            </button>
                        )
                    })
                }
            </div>
            <div className='border-2 border-[#EEEEEE] rounded-[12px]'>
                {
                    (documents || []).map((document) => {
                        return (
                            <div>
                                <img src={`images/docs.svg`} alt='logo' />
                                <div>
                                    <p className='text-[14px] text-black_500 font-medium'>
                                        {document.name}
                                    </p>
                                    <p className='text-[14px] font-medium'>
                                        {document.date}
                                    </p>
                                    <p className='text-[14px] font-medium'>
                                        {document.price}
                                    </p>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
}
