import { useState } from "react";

import NavBar from "./Navbar";
import Sidebar from "./Sidebar";


import Rightbar from "./Rightbar";
import MainSectior from "./MainSection";

const menuItems = [
  { id: 1, name: "Dashboard", content: "Welcome to the Dashboard!" },
  { id: 2, name: "Profile", content: "This is your Profile page." },
  { id: 3, name: "Settings", content: "Manage your settings here." },
];
export default function Dashboard() {
  const [selectedContent, setSelectedContent] = useState(menuItems[0].content);

  return (
    <div className="h-screen flex flex-col">
      {/* Navbar */}
      <nav className="h-16 bg-gray-800 text-white flex items-center justify-between px-6 fixed w-full top-0 z-50">
        <h1 className="text-xl font-bold">Dashboard</h1>
        <div className="space-x-4">
          <button className="text-sm px-4 py-2 bg-gray-700 rounded">Home</button>
          <button className="text-sm px-4 py-2 bg-gray-700 rounded">Logout</button>
        </div>
      </nav>

      <div className="flex flex-grow pt-16">
        {/* Sidebar */}
<Sidebar setSelectedContent={() =>setSelectedContent()} />
        {/* Main Section */}
       <MainSectior activeMenu={selectedContent} />

        {/* Right Bar */}
        <Rightbar />
      </div>
    </div>
  );
}
