        {menuItems.map((item) => (
          <div key={item.id} onClick={() => { setSelectedContent(item) }}
            className={`cursor-pointer pt-[13px] pb-[13px] !mt-0 flex items-center border-l-4 border-transparent flex-col 
           ${selectedContent?.id === item.id && 'bg-velvel_curtain border-l-4 border-white'}`}>
             <li><img src={`images/${item.icon}`} alt='logo' /><Link to={item.to} className={`!mt-0 text-[14px] font-[500] text-center sm:block hidden`}>    {item.name}</Link></li>
            {/* <img src={`images/${item.icon}`} alt='logo' />
