import React from "react"

interface SidebarProps {
  setSelectedContent: (id:number, content:string,name:string) => void
  menuItems:{id:number, content:string,name:string}[]
  selectedContent:string
}
const Sidebar: React.FC<SidebarProps> = ({
  setSelectedContent,
  menuItems,
  selectedContent
}) => {

    return (
        <div className='!bg-blue_500 w-[10%]'>
            <aside className="w-[10%] text-white h-screen fixed top-16">
            <img src={'images/site-logo.svg'} alt='logo' />
          <ul className="space-y-4">
            {menuItems.map((item) => (
              <li
                key={item.id}
                className={"p-3 text-[14px] font-[500] cursor-pointer hover:bg-velvel_curtain hover:border-l-8 border-white"}
                onClick={() => setSelectedContent(item)}
              >
                {item.name}
              </li>
            ))}
          </ul>
        </aside>
      </div>
    )
}

export default Sidebar
