import { useState } from "react";
import { Tabs } from "./Tabs";
import { TabsType } from "../constant/types";
import CustomButton from "../CustomButton";
// import { Clipboard } from "lucide-react";

const tabs = [
    { label: 'Bills', value: 'bills' },
    { label: 'Receipts', value: 'receipts' },
    { label: 'Bank', value: 'bank' }
  ]

const EasyUpload = () => {
  const [activeTab, setActiveTab] = useState("Receipts");
  const emailSingle = "abc.single@gmail.com";
  const emailMultiple = "abc.multiple@gmail.com";

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    alert("Copied to clipboard!");
  };


  return (
    <div className=" mx-auto bg-white p-6 rounded-xl shadow-md">
      <div className="flex pb-2 flex items-center">
      <p className="text-velvel_curtain text-sm font-bold">EasyUpload</p> 
      <Tabs tabs={tabs} tabsClick={(val: TabsType) => setActiveTab(val.value)} initialActive='receipts' className='w-full' />
      </div>

      <div className="mt-4 flex items-center justify-between">
        <div className="w-[50%] border-b  pb-[9px] mr-[15px]">
        <p className="text-velvel_curtain text-sm font-normal">Document Owner</p>
        <p className="font-semibold text-sm font-bold mt-3">Accountant 01</p>
        </div>
        <div className="flex items-start justify-between w-[50%] border-b  flex-col">
        <p className="text-gray-500  ml-[10px]">Client</p>
        <select className="w-full border-0 rounded-md p-2">
          <option>Select Client</option>
        </select>
        </div>
      </div>

      <div className="border-dashed border-2 border-gray-300 rounded-md mt-6 p-6 text-center">
        <div className="flex justify-center">
          <div className="bg-gray-100 p-4 rounded-full">
            ðŸ“¤
          </div>
        </div>
        <p className="text-purple-600 font-medium mt-2">Drag & Drop or Choose file to upload</p>
        <p className="text-gray-400 text-sm">JPG, PNG, PDF, CSV</p>
      </div>
      <div className="flex items-start justify-center">
      <CustomButton className="w-[25%] mt-4 bg-blue_500 text-white">
        Add Receipt
      </CustomButton>
      </div>

      <div className="mt-6">
        <p className="text-velvel_curtain text-sm font-normal">Send Over Email</p>
        <div className="flex items-center mt-2" onClick={()=>copyToClipboard(emailSingle)}>
          Single :<p className="text-blue_500"> {emailSingle}</p><img src={'images/copy.svg'} alt="left arrow" />
          {/* <Clipboard className="w-5 h-5 cursor-pointer" onClick={() => copyToClipboard(emailSingle)} /> */}
        </div>
        <div className="flex items-center mt-2">
          Multiple :<p className="text-blue_500"> {emailMultiple}</p><img src={'images/copy.svg'} alt="left arrow" />
          {/* <Clipboard className="w-5 h-5 cursor-pointer" onClick={() => copyToClipboard(emailMultiple)} /> */}
        </div>
      </div>
    </div>
  );
};

export default EasyUpload;
