import { useState } from "react"
import CustomButton from "../CustomButton"
import Input from "../CustomInput"
import { FormType } from "../constant/types"

const AddPhoneNumber = () => {
    const [newNumber, setNewNumber] = useState<FormType[]>([])
    const [values, setValues] = useState<FormType[]>([{ country: '', number: '', id:Date.now()}])


    const handleClick = () => {

        setNewNumber([...newNumber,{ country: '', number: '', id:Date.now()}])
    }

    const handleOnchange = (e:any,index:number) =>{
        const {name,value} = e.target
        let newitems = [ ...newNumber]

        newitems[index] = {
            ...newitems[index],
            [name]:value
        }
        setNewNumber(newitems)
    }

    const handleRemove = (id:number) =>{
            console.log(id,'dksdjasdkasd')
           const newVal =  newNumber.filter((item)=>item.id !== id)
           setNewNumber(newVal)
    }
    console.log(newNumber,'newNumbernewNumber')
    return (
        <div>
            <div className="flex mb-2 justify-between">
                <p className="text-[#0E253C] flex text-[12px] font-medium">
                    Phone Number
                </p>
                <div onClick={() => handleClick()} >
                <img src={`images/add-btn.svg`} className="cursor-pointer" alt='add' />
                </div>
            </div>
                {
                    newNumber.map((_item, index) => {
                        return (
                            <div key={index} className="flex items-center mb-2 gap-2">
                            <select onChange={(e) =>handleOnchange(e)} name="country" value={_item.country} >
                                <option><img src={`images/us-flag.svg`} alt='disk' />US</option>
                                <option><img src={`images/us-flag.svg`} alt='disk' />IN</option>
                            </select>
                            <Input name="number" className={''} onChange={(e) =>handleOnchange(e)} value={_item.number}  />
                            <CustomButton onClick={()=>handleRemove(_item?.id)} className="text-[12px] font-medium bg-transparent text-[#DC3545] border-[#DC3545]">Remove</CustomButton>
                        </div>)
                    })
                }
                <div className="border-b-[1px] border-[#0E253C1A] my-3" />
                <div className="flex flex-col">
                    <label className="text-[12px] font-medium mb-2">
                    Contact Owner
                    </label>
                            <select className="border-[#0E253C4D] border-[1px] rounded-[12px] h-[38px] px-4">
                                <option><img src={`images/us-flag.svg`} alt='disk' />US</option>
                                <option><img src={`images/us-flag.svg`} alt='disk' />IN</option>
                            </select>
                </div>
        </div>
    )
}

export default AddPhoneNumber
