import React, { useState } from 'react';
import { DocumentItem } from '../constant/types';

const tabs = [
    { label: 'All Documents', value: 'allDocuments' },
    { label: 'Bills', value: 'bills' },
    { label: 'Receipts', value: 'receipts' }
]

const documents = [
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 1', type: 'bills' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 2', type: 'bills' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 3', type: 'bills' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 1', type: 'receipts' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 3', type: 'receipts' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 3', type: 'receipts' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 1', type: 'bills' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Receipts 4', type: 'receipts' },
    { price: '₤ 50.59', date: 'Nov 1, 2019', name: 'Bill 4', type: 'bills' },
]
export default function CustomTabs() {
    const [active, setActive] = useState('allDocuments');
    const [filterTab, setFilterTab] = useState<DocumentItem[]>(documents)

    const handleClick = (tab: any) => {
        setActive(tab.value);
        if(tab.value === 'allDocuments'){
            setFilterTab(documents)
            return
        } 
       const filterData =  documents.filter((item)=> item.type === tab.value)
       setFilterTab(filterData)
    };

    return (
        <div className="w-[32%]">
            <div>
                {
                    (tabs || []).map((tab) => {
                        return (
                            <button
                                className={`text-[14px] h-[40px] text-velvel_curtain w-[112px] font-bold ${active === tab.value ? 'border-2 border-[#ECECF2] rounded-[8px]' : ''}`}
                                id={tab.value}
                                onClick={() => handleClick(tab)}
                            >
                                {tab.label}
                            </button>
                        )
                    })
                }
            </div>
            <div className='border-[1px] border-gray_200 rounded-[12px] mt-6'>
                {
                    (filterTab || []).map((document,index) => {
                        return (
                            <div key={index} className='flex p-4 pl-6 border-b-[1px] border-gray_200'>
                                <img src={`images/docs.svg`} alt='logo' />
                                <div className='flex flex-col ml-3'>
                                    <p className='text-[14px] text-black_500 font-medium'>
                                        {document.name}
                                    </p>
                                    <p className='text-[12px] text-velvel_curtain font-normal'>
                                       Date:  {document.date}
                                    </p>
                                    <p className='text-[12px] text-velvel_curtain font-normal'>
                                        {document.price}
                                    </p>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
}
