import React from 'react';

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  children: React.ReactNode;
  color?: string;
  backgroundColor?: string;
  className?: string;
}

const CustomButton: React.FC<ButtonProps> = ({
  children,
  color,
  backgroundColor,
  className,
  ...props
}) => {
  const combineClass = `h-[60px]  rounded-full font-semibold ${color || 'text-[#603AE5]'} border-0 w-52 ${backgroundColor || 'bg-[#603AE51A]'}`;

  return (
    <button className={combineClass} {...props}>
      {children}
    </button>
  );
};

export default CustomButton;
