import { useState } from "react";

import NavBar from "./Navbar";
import Sidebar from "./Sidebar";
import Rightbar from "./Rightbar";
import MainSectior from "./MainSection";
import { MenuItem } from "../constant/types";

const menuItems = [
  { id: 1, name: "Dashboard", content: "Welcome to the Dashboard!", icon:'Home.svg' },
  { id: 2, name: "Accounting", content: "This is your Profiqeqeqeqwle page.",icon:'account.svg' },
  { id: 3, name: "Banking", content: "Banking your settings here.",icon:'bank.svg' },
  { id: 4, name: "Reports", content: "Reports your settings here.",icon:'report.svg' },
  { id: 5, name: "Contacts", content: "Contacts your settings here.",icon:'contact.svg' },
  { id: 6, name: "Invoicing", content: "Contacts your settings here.",icon:'invoice.svg' },
  { id: 7, name: "Queries", content: "Queries your settings here.",icon:'querie.svg' },
];

export default function Dashboard() {
  const [selectedContent, setSelectedContent] = useState<MenuItem>(menuItems[0]);
  return (
    <div className="h-screen flex flex-col">
      {/* Navbar */}
     <NavBar />

      <div className="flex flex-grow ">
        {/* Sidebar */}
        <div className='!bg-blue_500 w-[7%]'>
      <aside className="w-[7%] text-white h-screen fixed top-16">
        <div className="flex justify-center mb-4">
        <img src={'images/site-logo.svg'} alt='logo' />
        </div>
        <ul className="space-y-4">
          {menuItems.map((item) => (
            <div key={item.id} onClick={() => { setSelectedContent(item) }} className={`cursor-pointer pt-[13px] pb-[13px] !mt-0 flex items-center flex-col ${selectedContent.id === item.id && 'bg-velvel_curtain border-l-8 border-white'}`}>
              <img src={`images/${item.icon}`} alt='logo' />
              <li
                className={`!mt-0 text-[14px] font-[500] text-center  `}
              >
                {item.name}
              </li>
            </div>
          ))}
        </ul>
      </aside>
    </div>
        {/* Main Section */}
       <main className="w-[81%]  p-5 pt-[120px] bg-gray-100 h-screen overflow-auto">
            <div className="bg-white pt-[26px] pr-[31px] pl-[21px] pb-[21px]">
                <div className="flex items-center justify-end">
                    <button className="text-[14px] font-medium w-[120px] bg-blue_500 text-white w-[180px]">Add to Expenses Report</button>
                </div>
                {/* <CustomTabs /> */}
            </div>
        </main>

        {/* Right Bar */}
        <aside className="w-1/10 bg-gray-200 p-4 h-screen fixed top-0 right-0">
      <p className="text-sm">Right Sidebar Content</p>
    </aside>
      </div>
    </div>
  );
}
