import { recentActivitie } from "../constant/constent";
import CustomButton from "../CustomButton";

const RecentActivities = () => {
    type ActivityItemProps = {
        iconColor: string;
        user: string;
        action: string;
        target: string;
        time: string;
        isRply?: boolean
    };

    const ActivityItem: React.FC<ActivityItemProps> = ({ iconColor, user, action, target, time, isRply }) => {
        return (
            <div className={`flex items-start gap-2 text-sm py-1 ${isRply && 'ml-3'}`}>

                {isRply ? <img src={`images/rply.svg`} className="cursor-pointer" alt='add' /> : <div className={`w-3 h-3 mt-1 rounded-full ${iconColor}`} />}
                <p className="text-gray-700">
                    <span className="text-[#513CCE] text-[14px] font-medium">{user}</span> {action}{' '}
                    <span className="text-[#513CCE] text-[14px] font-normal">{target}</span>{' '}
                    <span className="text-gray-500 flex"><p className="text-[20px]">â€¢ </p>{time}</span>
                </p>
            </div>
        );
    };

    return (
        <div className=' '>
            <div className='flex p-3 items-center justify-between border-b-[1px] border-[#0E253C1A]'>
                <p className='text-black_200 font-semibold text-[18px]'>Recent Activities</p>
                <div className=" border-[1px] border-[#0E253C1A] rounded-[12px] h-[38px]">
                    <CustomButton className="bg-white text-black_200 text-[14px] font-medium" >Message</CustomButton>
                    <CustomButton className="!text-[#0E253CD9] bg-[#F6F8F9] text-[14px] font-medium">Email</CustomButton>
                </div>
            </div>

            <div className="p-5">
                {Object.entries(recentActivitie).map(([month, txs]: any) => {
                    return (
                        <div key={month} >
                            <h3 className="text-xs text-gray-500 uppercase mb-1">{month}</h3>
                            <div>
                                {txs.map((tx: any, index: number) => (
                                    <div key={index}>
                                        <ActivityItem key={index} {...tx} />
                                    </div>
                                ))}
                            </div>
                        </div>
                    )
                }
                )}
            </div>
        </div>
    );
}

export default RecentActivities
