import React, { useState } from "react"

interface SidebarProps {
    setActiveMenu: (item:string) => void
}
const Sidebar: React.FC<SidebarProps> = ({
    setActiveMenu,
}) => {
  const menuItems = [
    { id: 1, name: "Dashboard", content: "Welcome to the Dashboard!" },
    { id: 2, name: "Profile", content: "This is your Profile page." },
    { id: 3, name: "Settings", content: "Manage your settings here." },
  ];
    // const menuItems = ["Dashboard", "Accounting", "Banking", "Reports","Contacts","Invoicing","Queries"];
    const [selectedContent, setSelectedContent] = useState(menuItems[0].content);


    return (
        <div className='bg-#603AE5 w-[10%]'>
            <img src={'images/site-logo.svg'} alt='logo' />
            <aside className="w-1/5 bg-gray-900 text-white p-5 h-screen fixed top-16">
          <ul className="space-y-4">
            {menuItems.map((item) => (
              <li
                key={item.id}
                className="p-3 bg-gray-700 rounded cursor-pointer hover:bg-gray-600"
                onClick={() => setSelectedContent(item.content)}
              >
                {item.name}
              </li>
            ))}
          </ul>
        </aside>
      </div>
    )
}

export default Sidebar
