import CustomButton from "../CustomButton";

 const RecentActivities = () => {
    type ActivityItemProps = {
        iconColor: string;
        user: string;
        action: string;
        target: string;
        time: string;
        isRply?:boolean
      };

      const ActivityItem: React.FC<ActivityItemProps> = ({ iconColor, user, action, target, time,isRply }) => {
        return (
          <div className="flex items-start gap-2 text-sm py-1">
            <div className={`w-3 h-3 mt-1 rounded-full ${iconColor}`} />
            {isRply && <img src={`images/rply.svg`} className="cursor-pointer" alt='add' />}
            <p className="text-gray-700">
              <span className="text-blue-700 font-semibold">{user}</span> {action}{' '}
              <span className="text-blue-600 font-semibold">{target}</span>{' '}
              <span className="text-gray-500">â€¢ {time}</span>
            </p>
          </div>
        );
      };

      const todayActivities = [
        { iconColor: 'bg-orange-500', user: 'Christian Wood', action: 'Sent Message to', target: 'Nikita Houston', time: '1 min ago',isRply:false },
        { iconColor: 'bg-gray-400', user: 'Nikita Houston', action: 'Replay your message', target: '"Hello"', time: '1 min ago',isRply:true },
        { iconColor: 'bg-red-500', user: 'Christian Wood', action: 'Accept Order from', target: 'Ebay', time: '1 min ago',isRply:false },
        { iconColor: 'bg-gray-400', user: 'Rohan Walker', action: 'Replay your message', target: 'Ebay', time: '1 min ago',isRply:true },
        { iconColor: 'bg-gray-400', user: 'Rohan Walker', action: 'Replay your message', target: 'Ebay', time: '1 min ago',isRply:true },
      ];

      const yesterdayActivities = [
        { iconColor: 'bg-purple-600', user: 'Christian Wood', action: 'Accept Order from', target: 'Amazon', time: '12:30PM' },
      ];

      const may25Activities = [
        { iconColor: 'bg-blue-600', user: 'Christian Wood', action: 'Accept Order from', target: 'Walmart', time: '11:20AM' },
      ];


    return (
            <div className=' '>
                  <div className='flex p-3 items-center justify-between border-b-[1px] border-[#0E253C1A]'>
                        <p className='text-black_200 font-semibold text-[18px]'>Recent Activities</p>
                        <div className=" border-[1px] border-[#0E253C1A] rounded-[12px] h-[38px]">
                            <CustomButton className="bg-white text-black_200 text-[14px] font-medium" >Message</CustomButton>
                            <CustomButton className="!text-[#0E253CD9] bg-[#F6F8F9] text-[14px] font-medium">Email</CustomButton>
                        </div>
                    </div>

                    <div>
        <h3 className="text-xs text-gray-500 uppercase mb-1">Today</h3>
        {todayActivities.map((act, i) => (
          <ActivityItem key={i} {...act} />
        ))}
      </div>

      <div className="mt-4">
        <h3 className="text-xs text-gray-500 uppercase mb-1">Yesterday</h3>
        {yesterdayActivities.map((act, i) => (
          <ActivityItem key={i} {...act} />
        ))}
      </div>

      <div className="mt-4">
        <h3 className="text-xs text-gray-500 uppercase mb-1">25 May 2024</h3>
        {may25Activities.map((act, i) => (
          <ActivityItem key={i} {...act} />
        ))}
      </div>
            </div>
    );
}

export default RecentActivities
