      <aside
        className={`bg-blue-900 text-white w-64 md:w-64 p-4 fixed md:relative h-full transition-transform ${
          sidebarOpen ? "translate-x-0" : "-translate-x-64"
        } md:translate-x-0`}
      >
        <h2 className="text-xl font-bold">Dashboard</h2>
        <ul className="mt-4 space-y-4">
          <li className="cursor-pointer p-2 rounded hover:bg-blue-700">Home</li>
          <li className="cursor-pointer p-2 rounded hover:bg-blue-700">Profile</li>
          <li className="cursor-pointer p-2 rounded hover:bg-blue-700">Settings</li>
          <li className="cursor-pointer p-2 rounded hover:bg-blue-700">Logout</li>
        </ul>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <nav className="bg-white shadow-md p-4 flex justify-between items-center">
          {/* Sidebar Toggle Button (Mobile) */}
          <button
            className="md:hidden bg-blue-900 text-white px-3 py-1 rounded"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            {sidebarOpen ? "‚úñ" : "‚ò∞"}
          </button>
          <h1 className="text-lg font-semibold">Dashboard</h1>
          <div className="flex space-x-4">
            <button className="text-gray-600 hover:text-black">üîî</button>
            <button className="text-gray-600 hover:text-black">‚öôÔ∏è</button>
            <button className="text-gray-600 hover:text-black">üë§</button>
          </div>
        </nav>

        {/* Page Content */}
        <main className="flex-1 p-6">
          <h2 className="text-2xl font-bold">Welcome to the Dashboard</h2>
          <p className="mt-2 text-gray-700">This is your main content area.</p>
        </main>
      </div>
    </div>
  );
}
