import { useState } from "react";

import NavBar from "./Navbar";
import Sidebar from "./Sidebar";
import Rightbar from "./Rightbar";
import MainSectior from "./MainSection";

const menuItems = [
  { id: 1, name: "Dashboard", content: "Welcome to the Dashboard!", icon:'Home.svg' },
  { id: 2, name: "Accounting", content: "This is your Profiqeqeqeqwle page.",icon:'account.svg' },
  { id: 3, name: "Banking", content: "Banking your settings here.",icon:'bank.svg' },
  { id: 4, name: "Reports", content: "Reports your settings here.",icon:'report.svg' },
  { id: 5, name: "Contacts", content: "Contacts your settings here.",icon:'contact.svg' },
  { id: 6, name: "Invoicing", content: "Contacts your settings here.",icon:'invoice.svg' },
  { id: 7, name: "Queries", content: "Queries your settings here.",icon:'querie.svg' },
];
export interface MenuItem {
  id: number;
  name: string;
  content: string;
  icon:string
}

export default function Dashboard() {
  const [selectedContent, setSelectedContent] = useState<MenuItem>(menuItems[0]);
  return (
    <div className="h-screen flex flex-col">
    {/* Top Section: Left Sidebar, Navbar, Right Sidebar */}
    <div className="flex w-full h-16 bg-gray-800 text-white fixed top-0 z-50">
      {/* Left Sidebar (20%) */}
      <aside className="w-1/5 bg-gray-900 flex items-center justify-center text-lg font-semibold">
        Left Sidebar
      </aside>

      {/* Navbar (Centered, 70%) */}
      <nav className="w-7/10 flex items-center justify-center text-xl font-bold">
        Dashboard
      </nav>

      {/* Right Sidebar (10%) */}
      <aside className="w-1/10 bg-gray-200 flex items-center justify-center text-gray-700">
        Right Sidebar
      </aside>
    </div>

    {/* Main Content (70%) */}
    <div className="flex flex-grow mt-16">
      {/* Left Sidebar Navigation */}
      <aside className="w-1/5 bg-gray-900 text-white p-5 h-screen">
        <ul className="space-y-4">
          {menuItems.map((item) => (
            <li
              key={item.id}
              className="p-3 bg-gray-700 rounded cursor-pointer hover:bg-gray-600"
              onClick={() => setSelectedContent(item)}
            >
              {item.name}
            </li>
          ))}
        </ul>
      </aside>

      {/* Main Content Area (70%) */}
      <main className="w-7/10 p-6 bg-gray-100 h-screen overflow-auto">
        <div className="bg-white shadow-md p-6 rounded-lg">
          {selectedContent.content}
        </div>
      </main>

      {/* Right Sidebar (10%) */}
      <aside className="w-1/10 bg-gray-200 p-4 h-screen">
        <p className="text-sm">Right Sidebar Content</p>
      </aside>
    </div>
  </div>
  );
}
