import React, { useState } from 'react';
import CustomButton from '../CustomButton';
import { TabsType } from '../constant/types';

interface TabsProps {
    tabs: { label: string, value: string }[],
}

export const Tabs: React.FC<TabsProps> = ({
    tabs
}) => {
    const [active, setActive] = useState('allDocuments');

    const handleClick = (tab: TabsType) => {
        setActive(tab.value);
    };

    return (
        <div className="w-[32%]">
            <div>
                {
                    (tabs || []).map((tab, index) => {
                        return (
                            <CustomButton
                                key={index}
                                className={`text-[14px] !bg-transparent h-[40px] text-velvel_curtain w-[112px] font-bold ${active === tab.value ? 'border-2 border-[#ECECF2] rounded-[8px]' : ''}`}
                                id={tab.value}
                                onClick={() => handleClick(tab)}
                            >
                                {tab.label}
                            </CustomButton>
                        )
                    })
                }
            </div>
        </div>
    );
}
