import React, { useState } from 'react';
// import './tabs.css';

export default function CustomTabs() {
    const tabs = [
        {label:'All Documents',value:'allDocuments'},
        {label:'Bills',value:'bills'},
        {label:'Receipts',value:'receipts'}
    ]
    const [active, setActive] = useState('allDocuments');

  const handleClick = (tab:any) => {
    setActive(tab.value);
  };

  return (
    <div>
      <div className="container">
        <div className="tabs">
            {
                (tabs || []).map((tab)=>{
                    return(
                        <button
                        className={`text-[14px] text-velvel_curtain w-[112px] font-bold ${active === tab.value ? 'border-1 border-[#ECECF2]' : ''}`}
                        id={tab.value}
                        onClick={()=>handleClick(tab)}
                      >
                        {tab.label}
                      </button>
                    )
                })
            }
        </div>
      </div>
    </div>
  );
}
