import React from "react"
import { MenuItem } from "./Dashboard"

interface SidebarProps {
  setSelectedContent: (item:MenuItem) => void
  menuItems:{id:number, content:string,name:string,icon:string}[]
  selectedContent:MenuItem
}
const Sidebar: React.FC<SidebarProps> = ({
  setSelectedContent,
  menuItems,
  selectedContent
}) => {

    return (
        <div className='!bg-blue_500 w-[10%]'>
            <aside className="w-[10%] text-white h-screen fixed top-16">
            <img src={'images/site-logo.svg'} alt='logo' />
          <ul className="space-y-4">
            {menuItems.map((item) => (
              <>
              <img src={`images/${item.icon}`} alt='logo' />
              <li
                key={item.id}
                className={`p-3 pt-0 text-[14px] font-[500] text-center cursor-pointer ${selectedContent.id === item.id && 'bg-velvel_curtain border-l-8 border-white'}`}
                onClick={() => {setSelectedContent(item)}}
              >
                {item.name}
              </li>
              </>
            ))}
          </ul>
        </aside>
      </div>
    )
}

export default Sidebar
